import {mapGetters,mapActions} from 'vuex'
import close from '@/assets/yard/closeWin.svg?inline'
import AFormItem from 'ant-design-vue/es/form/FormItem'
import ARow from 'ant-design-vue/es/grid/Row'
import { addFeedBack } from '@api/common'
import ATextarea from 'ant-design-vue/es/input/TextArea'
export default {
  name: 'EditModal',
  components: { ATextarea, ARow, AFormItem },
  data(){
    return {
      confirmLoading:false,
      close,
      text:''
    }
  },
  methods:{
    ...mapGetters(['showOpinionModal','userInfo']),
    ...mapActions(['showOpinion']),
    init(){

    },
    handleCancel(){

    },
    handlerFeedback(){
      if(this.text.length<=0){
        this.$notification.warning({
          duration:4,
          message:"意见不能为空"
        });
        return
      }
      const params={
        userId:this.userInfo().id,
        feedback:this.text
      }
      addFeedBack(params).then(res=>{
        if(res.code==200){
          this.$notification.success({
            duration:4,
            message:"意见反馈成功,感谢您为我们的产品提出宝贵意见！"
          });
          this.showOpinion(false);
          this.text="";
        }else{
          this.$notification.warning({
            duration:4,
            message:res.message
          });
        }
      }).catch(e=>{
        console.error("网络错误");
        this.$notification.error({
          duration:4,
          message:"意见反馈失败，网络错误"
        });
      })
    },
    closeWin(){
      this.showOpinion(false);
      this.text="";
    }
  },
  destroyed(){
    if(this.sendCode){
      clearInterval(this.sendCode);
    }
  },
  created () {
    this.init()

  }
}
